changeSet.sql:
create table t_vaccine_type
(
    id bigint default nextval('vaccineTypeIdGenSeq') not null,
    name varchar(33) not null,
    information_before_take text not null,
    way_of_giving text not null,
    NOP_information text not null
);

create unique index t_vaccine_type_id_uindex
on t_vaccine_type (id);

create unique index t_vaccine_type_name_uindex
on t_vaccine_type (name);

alter table t_vaccine_type
add constraint t_vaccine_type_pk
primary key (id);
